import React, { Component } from 'react'
import axios from 'axios'

export default class App extends Component {
  state ={
    advice :[]
  }

  getAdvice =()=>{
    axios.get("https://api.adviceslip.com/advice",).then(advice=>{
      console.log(advice.data.slip)
      this.setState({advice:advice.data.slip})


    })
  }
  showAdvice=()=>{

      return this.state.allTheBeers.map(eachBeer=>{
          return(

              <li>
              <h4>{eachBeer.name}</h4>
              <img src={eachBeer.image_url} width="200px" alt={eachBeer.name}/>
              <p>{eachBeer.description}</p>
              </li>

          )
      })


  }

  render() {
    return (
      <div>
        {this.getAdvice()}

      </div>
    )
  }
}
