      this.setState({advice:advice.data.slip})

      console.log(advice.data.slip)


    })

  }

  getAdvice=()=>{
    axios.get("https://api.adviceslip.com/advice").then(advice=>{
      this.setState({advice:advice.data.slip})

      console.log(advice.data.slip)
     axios.post("https://ironrest.herokuapp.com/createCollection/johnny").then(res=>{
       console.log(res.data)
     })

    })
  }


  render() {
    return (
      <div className="App">
        <nav>COOL NAV BAR!!</nav>
        {this.state.advice.advice}
        <div>
        <button onClick={this.getAdvice}>Click Here !!!</button>
        </div>


      </div>
    )
  }
}
