    //       )
    //   })

    // }

    showTheList = () => {
        const fullList = this.state.theList.map((profile) => {
            return (
            <div className="list-group-item list-group-item-action" key={profile._id}> {profile.firstName} 
            </div>


            )
        })
        console.log(fullList)
        return fullList

      }


  render() {
      return (

              <div className="container">
          <div className="row">
            <div className="col-5" >
              <div className="list-group">
              {this.showTheList()} 
              </div>
            </div>



            <div className="details">
                <div className="col-7">
                    <h1>{this.state.thePerson.name}</h1>
                    <table className="table">
                    <thead></thead>
                        <tbody>
                            <tr>
                            <td >Capital</td>
                            <td>{this.state.dog}</td>
                            </tr>
                            <tr>
                            <td>Area</td>
                            <td>{this.state.thePerson.gif}
                                <sup>2</sup>
                            </td>
                            </tr>
                            <tr>
                            <td>Borders</td>
                            <td>
                                <ul>
                                    {this.state.thePerson.advice}
                                </ul>
                            </td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>





          </div>
        </div>



      )
  }
}
