            return aCountry.id === id
        })
    }



    render() {
        //console.log(this.props)
        let id = this.props.match.params.id; 
        console.log(' use ',id, " to find the right country ", list)
        console.log(list[0].id)

        let theCountry = this.findCountry(id)


