    //           {profile.firstName}  
    //     </Link>
    //       )
    //   })

    // }

    showTheList = () => {
        const fullList = this.state.theList.map((profile,i) => {
            return (
            <Link className="list-group-item list-group-item-action" key={i} to={`/list/${profile.id}`}> {profile.firstName}  
            </Link>
            )
        })
        return fullList
      }


  render() {
      return (

              <div className="container">
          <div className="row">
            <div className="col-5" >
              <div className="list-group">
              {this.showTheList()} 
              </div>
            </div>

            <Switch>
              <Route exact path="/country/:id" component={personDetails}/>
            </Switch>



          </div>
        </div>



      )
  }
}
