        image:null,
        gif:null,
    }

    handleChange=(e)=> {
        console.log(e.target.value);
        //console.log(e.target.id)
        //if(e.target.id===firstName)

        switch (e.target.value){
            case "firstName":

                    this.setState({

                        firstName:e.target.value

                    });
                    break;
                    case "lastName":

                    this.setState({

                        lastName:e.target.value

                    });
                    break;
                    case "advice":

                    this.setState({

                        advice:e.target.value

                    });
                    break;
                    case "dog":

                    this.setState({

                        dog:e.target.value

                    });
                    break;
                    case "gif":

                    this.setState({

                        firstName:e.target.value

                    });
                    break;


                    default:
                        console.log("")
        }

        // this.setState({

